package Calculator;

// for reading inputs from standard input
import java.util.Scanner;

// for mathematical operations
import java.lang.Math;

/* This code is generated by Hassan Zaker and Jason Balaci as part of the course
 * requirements of CAS 703 (a graduate-level course at McMaster University, 
 * hosted by Dr. Richard Paige). 
 */
public class Calculator {

  public static void main(String[] args) {
    Scanner scanner = new Scanner(System.in);

    // introduction
    System.out.println("Welcome to the Example Calculator Calculator Program!");
    System.out.println("Calculating things, an example :)");
    System.out.println("Please see the related README.md file for more information.");

    // input symbols
    System.out.println("Please input x : double");
    System.out.println(" ~ x = some expected input variable");
    double x = scanner.nextDouble();
    scanner.close();

    // intermediate symbols
    double y;
    boolean p;

    // output symbols
    double z;

    System.out.println("Calculating...");

    y = (x + 1.23);
    System.out.println("y ~ " + y);
    z = (x * Math.pow(x, 1.23));
    p = (z == z);
    z = p ? 1.23 : z;
    
    System.out.println("...ok!");

    System.out.println("Outputs:");
    System.out.println("z = " + z);
  }
}
